import React, { useState, useEffect } from "react";

import "./ExpenseForm.css";

const ExpenseForm = (props) => {
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");

  //form validation
  const [titleDirty, setTitleDirty] = useState(false);
  const [amountDirty, setAmountDirty] = useState(false);
  const [titleError, setTitleError] = useState("Can not be empty");
  const [amountError, setAmountError] = useState("Can not be empty");
  const [formValid, setFormValid] = useState(false);

  function setTextToPlaceholderTitle() {
    return <>{titleError}</>;
  }

  useEffect(() => {
    if (titleError || amountError) {
      setFormValid(false);
    } else {
      setFormValid(true);
    }
  }, [titleError, amountError]);

  let placeholderTitle;
  let placeholderAmount;

  const blurHandler = (e) => {
    switch (e.target.name) {
      case "title":
        setTitleDirty(true);
        break;
      case "amount":
        setAmountDirty(true);
        break;
    }
  };

  const titleChangeHandler = (event) => {
    setEnteredTitle(event.target.value);
    if (event.target.value === "") {
      setTitleError("Enter the title");
    } else {
      setTitleError("");
    }
  };

  const amountChangeHandler = (event) => {
    setEnteredAmount(event.target.value);
    if (event.target.value === 0) {
      setAmountError("Can not be empty");
    } else {
      setAmountError("");
    }
  };

  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const expenseData = {
      title: enteredTitle,
      amount: +enteredAmount,
      date: new Date(enteredDate),
    };

    props.onSaveExpenseData(expenseData);
    setEnteredTitle("");
    setEnteredAmount("");
    setEnteredDate("");
  };

  return (
    <>
      <form onSubmit={submitHandler}>
        <div className="new-expense__controls">
          <div className="new-expense__control">
            <label>Title</label>
            {titleDirty && titleError && (
              <div style={{ color: "red" }}>
                {" "}
                {(placeholderTitle = titleError)}
              </div>
            )}
            <input
              placeholder={setTextToPlaceholderTitle}
              name="title"
              type="text"
              value={enteredTitle}
              onChange={titleChangeHandler}
              onBlur={(e) => blurHandler(e)}
            />
          </div>
          <div className="new-expense__control">
            <label>Amount</label>
            {amountDirty && amountError && (
              <div style={{ color: "red" }}>
                {(placeholderAmount = amountError)}
              </div>
            )}
            <input
              name="amount"
              type="number"
              min="0.01"
              step="0.01"
              value={enteredAmount}
              onChange={amountChangeHandler}
              onBlur={(e) => blurHandler(e)}
            />
          </div>
          <div className="new-expense__control">
            <label>Date</label>
            <input
              type="date"
              min="2019-01-01"
              max="2023-12-31"
              value={enteredDate}
              onChange={dateChangeHandler}
            />
          </div>
        </div>
        <div className="new-expense__actions">
          <button type="button" onClick={props.onCancel}>
            Cancel
          </button>
          <button type="submit" disabled={!formValid}>
            Add Expense
          </button>
        </div>
      </form>
    </>
  );
};

export default ExpenseForm;
